    UIColor* blue = [UIColor colorWithRed: 0.153 green: 0.565 blue: 0.682 alpha: 1];
    UIColor* midnight = [UIColor colorWithRed: 0 green: 0 blue: 0.039 alpha: 1];

    //// Gradient Declarations
    CGFloat gradientLocations[] = {0, 0.5, 1};
    CGGradientRef gradient = CGGradientCreateWithColors(colorSpace, (__bridge CFArrayRef)@[(id)green.CGColor, (id)blue.CGColor, (id)midnight.CGColor], gradientLocations);

    //// backgroundGradient Drawing
