        CGPointMake(CGRectGetMidX(backgroundGradientRect), CGRectGetMinY(backgroundGradientRect)),
        0);
    CGContextRestoreGState(context);


    //// Cleanup
    CGGradientRelease(gradient);
    CGColorSpaceRelease(colorSpace);
}

